package ejComentarios;

public class ejComentarios {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 10; // variable llamada num1 declarada como int que tiene como valor 10
		int num2 = 20; // variable llamada num2 declarada como int que tiene como valor 20
		int suma = num1 + num2; // variable llamada suma declarada como int que tiene como valor la suma de num1 y num2
		System.out.println(suma); //Mostramos por consola el resultado de suma.
	}

}
