package ejConstantes;

import java.lang.constant.Constable;

public class ejConstantes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Logicos
				boolean interruptor = false;
				System.out.println(interruptor);
				//Caracter
				final char VARIABLECHAR = 'a';
				System.out.println(VARIABLECHAR);
				//Numericos enteros
				final byte VARIABLEBYTE = 125;
				System.out.println(VARIABLEBYTE);
				final short VARIABLESHORT = 30000;
				System.out.println(VARIABLESHORT);
				final int VARIABLEINT = 33000;
				System.out.println(VARIABLEINT);
				final long VARIABLELONG = 4444444L;
				System.out.println(VARIABLELONG);
				//Numericos reales
				final float VARIABLEFLOAT = 0.123456789f;
				System.out.println(VARIABLEFLOAT);
				final double VARIABLEDOUBLE = 0.1234567898765432111;
				System.out.println(VARIABLEDOUBLE);
				// Constantes
				final Constable VARIABLECONSTANTE = Math.PI;
				System.out.println(VARIABLECONSTANTE);
	}

}
