package ejTiposPrimitivos;

import java.lang.constant.Constable;

public class ejTiposPrimitivos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Logicos
		boolean interruptor = false;
		System.out.println(interruptor);
		//Caracter
		char variableChar = 'a';
		System.out.println(variableChar);
		//Numericos enteros
		byte variableByte = 125;
		System.out.println(variableByte);
		short variableShort = 30000;
		System.out.println(variableShort);
		int variableInt = 33000;
		System.out.println(variableInt);
		long variableLong = 4444444L;
		System.out.println(variableLong);
		//Numericos reales
		float variableFloat = 0.123456789f;
		System.out.println(variableFloat);
		double variableDouble = 0.1234567898765432111;
		System.out.println(variableDouble);
		// Constantes
		Constable variableConstante = Math.PI;
		System.out.println(variableConstante);
	}

}
